        this._localeSub = localeModel.subscribe((locale) =&gt; {
              this.setState({locale: locale});
          }
        );
    }

    componentWillUnmount() {
        this._localeSub.dispose();
    }

    suffix(pl) {
        var l = pl || this.state.locale;
        if (!l) {
            return 'us';
