        console.log('choosing ', localeName);
        localeModel.onNext(localeName);
    }

    render() {
        const flagClasses = classnames('flag', 'flag-' + this.suffix());
        const menuClasses = classnames('menu', this.state.showMenu ? 'show' : 'hide');
        console.log('rendering with menu classes ', menuClasses, this.state);
        const choose = this.choose.bind(this);

        let menuOptions = localeList.map((localeName, i) =&gt; {
            const menuFlagClasses = classnames('flag', 'flag-' + this.suffix(localeName));

            function chooseLL(event) {
                event.stopPropagation();
                choose(localeName);
            }

            return &lt;div key={'flagMenu' + i} className={menuFlagClasses} onClick={chooseLL}&gt;&lt;/div&gt;;
        });

        return (&lt;div className={flagClasses} onClick={this.showMenuClick.bind(this)}&gt;
            &lt;div className={menuClasses}&gt;
                {menuOptions}
            &lt;/div&gt;
        &lt;/div&gt;);
    }

}

export default LocaleMenu;

